const mongoose = require('mongoose')
mongoose.connect('mongodb://127.0.0.1:27017/moviesd')
.then(() => console.log('connection open')).catch((err) => console.log("Connection failed"))
const series_schema = new mongoose.Schema({
    name: String,
    date: Number,
    ratings: Number,
    isWatched: Boolean
});
const serie = mongoose.model('Serie', series_schema);
